import pandas as pdimport matplotlib.pyplot as pltimport tushare as tsimport talibstockreview = ts.get_k_data('601985', start='2018-01-01', end='2018-05-11')print(stockreview.shape)atr5 = talib.ATR(stockreview.high.values, stockreview.low.values, stockreview.close.values, timeperiod=5)atr10 = talib.ATR(stockreview.high.values, stockreview.low.values, stockreview.close.values, timeperiod=10)atr14 = talib.ATR(stockreview.high.values, stockreview.low.values, stockreview.close.values, timeperiod=14)atr21 = talib.ATR(stockreview.high.values, stockreview.low.values, stockreview.close.values, timeperiod=21)pd.DataFrame({'close': stockreview.close, 'atr5':atr5, 'atr10':atr10, 'atr14': atr14, 'atr21':atr21}).plot(subplots=True, grid=True)plt.show()